import React, { useState } from 'react';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import GoogleAuth from './components/GoogleAuth';
import GmailView from './components/GmailView';
import DriveView from './components/DriveView';
import YouTubeView from './components/YouTubeView';
import './App.css';

function App() {
  const [accessToken, setAccessToken] = useState(null);

  const handleTokenUpdate = (token) => {
    setAccessToken(token);
  };

  return (
    <GoogleOAuthProvider clientId="577155825398-pg20jk65d3ksgf16vbtirnmvraq8kvff.apps.googleusercontent.com">
      <Router>
        <div className="App">
          <header className="App-header">
            <h1>Google Authentication Demo</h1>
            <Routes>
              <Route path="/" element={<GoogleAuth onTokenUpdate={handleTokenUpdate} />} />
              <Route
                path="/gmail"
                element={accessToken ? <GmailView accessToken={accessToken} /> : <Navigate to="/" />}
              />
              <Route
                path="/drive"
                element={accessToken ? <DriveView accessToken={accessToken} /> : <Navigate to="/" />}
              />
              <Route
                path="/youtube"
                element={accessToken ? <YouTubeView accessToken={accessToken} /> : <Navigate to="/" />}
              />
            </Routes>
          </header>
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
}

export default App;
