import React, { useState } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';

const GoogleAuth = ({ onTokenUpdate }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const handleSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    onTokenUpdate(credentialResponse.credential);
  };

  const handleError = () => {
    console.log('Login Failed');
  };

  const handleLogout = () => {
    setUser(null);
    onTokenUpdate(null);
  };

  const handleServiceClick = (service) => {
    navigate(`/${service}`);
  };

  return (
    <div className="google-auth-container">
      {!user ? (
        <div className="login-button">
          <GoogleLogin
            onSuccess={handleSuccess}
            onError={handleError}
            scope="email profile https://www.googleapis.com/auth/gmail.readonly https://www.googleapis.com/auth/drive.readonly https://www.googleapis.com/auth/youtube.readonly"
          />
        </div>
      ) : (
        <div className="user-info">
          <h2>Welcome {user.name}!</h2>
          <p>Email: {user.email}</p>
          <img src={user.picture} alt="profile" />
          <div className="service-buttons">
            <button onClick={() => handleServiceClick('gmail')}>Access Gmail</button>
            <button onClick={() => handleServiceClick('drive')}>Access Drive</button>
            <button onClick={() => handleServiceClick('youtube')}>Access YouTube</button>
          </div>
          <button onClick={handleLogout} className="logout-button">Logout</button>
        </div>
      )}
    </div>
  );
};

export default GoogleAuth;
