import React, { useState } from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';

const API_URL = 'http://localhost:8011';

const GoogleAuth = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSuccess = async (credentialResponse) => {
    try {
      setLoading(true);
      setError(null);

      // Decode the JWT token
      const decoded = jwtDecode(credentialResponse.credential);
      console.log('Decoded token:', decoded);

      // Send the credential to our backend
      const response = await fetch(`${API_URL}/api/auth/google/callback`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          code: credentialResponse.credential
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to authenticate with server: ${errorText}`);
      }

      const data = await response.json();
      console.log('Authentication successful:', data);

      // Combine Google profile data with our backend data
      setUser({ ...decoded, ...data });

      // Store the token for later use
      localStorage.setItem('googleToken', credentialResponse.credential);

    } catch (err) {
      console.error('Authentication error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleError = () => {
    console.log('Login Failed');
    setError('Login Failed');
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('googleToken');
  };

  return (
    <div className="google-auth-container">
      {!user ? (
        <div className="login-button">
          <GoogleLogin
            onSuccess={handleSuccess}
            onError={handleError}
          />
          {loading && <p>Loading...</p>}
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      ) : (
        <div className="user-info">
          <h2>Welcome {user.name}!</h2>
          <p>Email: {user.email}</p>
          {user.picture && <img src={user.picture} alt="profile" style={{ width: '50px', borderRadius: '50%' }} />}
          <button onClick={handleLogout} style={{ marginLeft: '10px' }}>Logout</button>
        </div>
      )}
    </div>
  );
};

export default GoogleAuth;
