import React, { useState, useEffect } from 'react';
import { google } from 'googleapis';

const GmailView = ({ accessToken }) => {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const gmail = google.gmail({ version: 'v1', headers: {
          Authorization: `Bearer ${accessToken}`
        }});

        const response = await gmail.users.messages.list({
          userId: 'me',
          maxResults: 10
        });

        const emailPromises = response.data.messages.map(async (message) => {
          const emailData = await gmail.users.messages.get({
            userId: 'me',
            id: message.id
          });
          return emailData.data;
        });

        const emailDetails = await Promise.all(emailPromises);
        setEmails(emailDetails);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    if (accessToken) {
      fetchEmails();
    }
  }, [accessToken]);

  if (loading) return <div>Loading emails...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="gmail-container">
      <h2>Recent Emails</h2>
      <div className="email-list">
        {emails.map((email) => (
          <div key={email.id} className="email-item">
            <h3>{email.payload.headers.find(h => h.name === 'Subject')?.value}</h3>
            <p>From: {email.payload.headers.find(h => h.name === 'From')?.value}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GmailView;
